sections.css:
.ec-headingTitle{
  margin: 0 0 8px;
  font-size: 32px;
  font-weight: normal;
  color: #525263;
}

.ec-pageHeader h1{
  margin: 0 0 8px;
  border-bottom: 1px dotted #ccc;
  border-top: 1px solid #ccc;
  padding: 8px 0 12px;
  font-size: 16px;
  font-weight: bold;
  @include media_desktop {
    border-top: none;
    border-bottom: 1px solid #ccc;
    margin: 10px 16px 48px;
    padding: 8px;
    font-size: 32px;
    font-weight: bold;
  }
}

.ec-heading{
  margin: 24px 0;
}

.ec-heading-bold {
  margin: 16px 0;
  font-size: 16px;
  font-weight: bold;
  @include media_desktop {
    font-size: 18px;
  }
}

.ec-rectHeading{
  h1, h2, h3,
  h4, h5, h6{
    background: $clrGray;
    padding: 8px 12px;
    font-size: 20px;
    font-weight: bold;
  }

}

.ec-reportHeading{
  width: 100%;
  border-top: 1px dotted #ccc;
  margin: 20px 0 30px;
  padding: 0;
  text-align: center;
  font-size: 24px;
  font-weight: bold;
  @include media_desktop {
    border-top: 0;
    font-size: 32px;
  }
  h1, h2, h3,
  h4, h5, h6,p {
    font-weight: bold;
    font-size: 24px;
    @include media_desktop {
      font-size: 32px;
    }
  }
}

.ec-link {
  color: #0092C4;
  text-decoration: none;
  cursor: pointer;
  &:hover {
    color: #33A8D0;
    text-decoration: none;
  }
}

.ec-font-bold {
  font-weight: bold;
}

.ec-color-grey {
  color: #9a947e;
}

.ec-color-red {
  color: #DE5D50;
}

.ec-color-accent {
  color: #DE5D50;
}

.ec-font-size-1 {
  font-size: 12px;
}

.ec-font-size-2 {
  font-size: 14px;
}

.ec-font-size-3 {
  font-size: 16px;
}

.ec-font-size-4 {
  font-size: 20px;
}

.ec-font-size-5 {
  font-size: 32px;
}

.ec-font-size-6 {
  font-size: 40px;
}

.ec-text-ac {
  text-align: center;
}

.ec-price {
  & &__unit {
    font-size: 18px;
    font-weight: bold;
    @include media_desktop{
      font-size: 1em;
    }
  }
  & &__price {
    display: inline-block;
    padding: 0 .3em;
    font-size: 18px;
    font-weight: bold;
    @include media_desktop{
      font-size: 1em;
    }
  }
  & &__tax {
    font-size: 10px;
    @include media_desktop{
      font-size: 0.57em;
    }
  }

}

.text-left {
  text-align: left;
}

.text-center {
  text-align: center;
}

.text-right {
  text-align: right;
}

.ec-reportDescription {
  margin-bottom: 32px;
  text-align: center;
  font-size: 16px;
  line-height: 1.4;
}

.ec-para-nomal {
  margin-bottom: 16px;
}

.ec-definitions {
  margin: 5px 0;
  display: block;
  & dt, dd {
    display: inline-block;
    margin: 0;
  }
  & dt {
    font-weight: bold;
  }
}

.ec-definitions--soft {
  @extend .ec-definitions;
  & dt {
    font-weight: normal;
  }
}

.ec-borderedDefs {
  width: 100%;
  border-top: 1px dotted #ccc;
  margin-bottom:16px;
  dl {
    display: flex;
    border-bottom: 1px dotted #ccc;
    margin: 0;
    padding: 10px 0 0;
    flex-wrap: wrap;
    @include media_desktop {
      flex-wrap: nowrap;
      padding: 15px 0 4px;
    }
  }
  dt, dd {
    padding: 0;
  }

  dt {
    font-weight: normal;
    width: 100%;
    padding-top: 0;
    @include media_desktop {
      padding-top: 14px;
      width: 30%;
    }
  }

  dd {
    padding: 0;
    width: 100%;
    line-height: 2.5;
    @include media_desktop {
      width: 70%;
      //padding: 18px 16px;
      line-height: 3;
    }
  }
  p {
    line-height: 1.4;
  }
}

.ec-list-chilled {
  display: table-row;
  border: 0 none;
  padding: 8px 0;

  dt, dd {
    display: table-cell;
    border-bottom: 1px dotted #ccc;
    padding: 0;
    @include media_desktop {
      padding: 16px 0;
    }
  }

  dt {
    width: 30%;
  }

  dd {
    padding: 0;
    @include media_desktop {
      padding: 16px;
    }
  }
}

.ec-borderedList {
  width: 100%;
  border-top: 0;
  list-style: none;
  padding: 0;
  @include media_desktop {
    border-top: 1px dotted #ccc;
  }
  li {
    border-bottom: 1px dotted #ccc;
  }
}

.ec-list-chilled {
  display: table-row;
  border: 0 none;
  padding: 8px 0;

  dt, dd {
    display: table-cell;
    border-bottom: 1px dotted #ccc;
    padding: 16px 0;
  }

  dt {
    width: 30%;
  }

  dd {
    padding: 16px;
  }
}

.ec-inlineBtn{
  @include btn-default;
}
.ec-inlineBtn--primary{
  @include btn-primary
}
.ec-inlineBtn--action{
  @include btn-action
}
.ec-inlineBtn--cancel{
  @include btn-cancel
}

.ec-blockBtn{
  @include blockBtn-default;
}
.ec-blockBtn--primary{
  @include blockBtn-primary
}
.ec-blockBtn--action{
  @include blockBtn-action
}
.ec-blockBtn--cancel{
  @include blockBtn-cancel
}

.ec-closeBtn{
  cursor: pointer;
  .ec-icon {
    img {
      //overflow: hidden;
      display: inline-block;
      margin-right: 5px;
      width: 1em;
      height: 1em;
      position: relative;
      top: -1px;
      vertical-align: middle;
    }
  }
}

.ec-closeBtn--circle{
  display: block;
  border: 0 none;
  padding: 0;
  margin: 0;
  text-shadow: none;
  box-shadow: none;
  border-radius: 50%;
  background: #B8BEC4;
  cursor: pointer;
  width: 40px;
  min-width: 40px;
  max-width: 40px;
  height: 40px;
  line-height: 40px;
  vertical-align: middle;
  position: relative;
  text-align: center;

  .ec-icon img{
    display: block;
    margin-top: -.5em;
    margin-left: -.5em;
    width: 1em;
    height: 1em;
    position: absolute;
    top: 50%;
    left: 50%;
  }
}

.ec-blockTopBtn{
  position: fixed;
  width:120px;
  height: 40px;
  right: 0;
  bottom: 10px;
  cursor: pointer;
  color: #FFFFFF;
  text-align: center;
  line-height: 40px;
  opacity: 0.8;
  background-color: #9da3a9;
  @include media_desktop {
    right:30px;
    bottom: 30px;
  }

  &:after{
    content: "ページトップへ";
  }
}

.ec-input{
  @include forms-reset;
  @include form-controls;
  input{
    height: 40px;
    font-family: Roboto, "游ゴシック", YuGothic, "Yu Gothic", "ヒラギノ角ゴ ProN W3", "Hiragino Kaku Gothic ProN", Arial, "メイリオ", Meiryo, sans-serif;
    margin-bottom: 10px;
    @include media_desktop {
      margin-bottom: 16px;
    }
  }
  textarea {
    height: auto;
    min-height: 100px;
  }
  p {
    line-height: 1.4;
  }
  .ec-errorMessage {
    margin-bottom: 25px;
    font-size: 12px;
    font-weight: bold;
    color: $clrRed;
  }
}
.error.ec-input {
  input,select{
    margin-bottom: 5px;
    border-color: #CF3F34;
    background: #FDF1F0;
  }
}

.ec-halfInput{
  @extend .ec-input;
  input[type='text']{
    display: inline-block;
    width: 47%;
    margin-left: 2%;
    @include media_desktop {
      margin-left: 15px;
      width: 45%;
    }
  }
  input[type='text']:first-child{
    margin-left: 0;
  }
}

.ec-numberInput{
  @extend .ec-input;
  input[type='number']{
    display: inline-block;
    width: auto;
    max-width: 100px;
    text-align: right;
  }
}

.ec-zipInput{
  @extend .ec-input;
  display: inline-block;
  input{
    display: inline-block;
    width: auto;
    text-align: right;
    max-width: 4em;
    font-size: 16px;
    @include media_desktop {
      max-width: 5em;
    }
  }
  span{
    display: inline-block;
    padding: 0 5px 0 3px;
    margin-left:5px;
  }
}
.ec-zipInputHelp {
  display: inline-block;
  margin-left: 10px;
  margin-bottom: 16px;
  vertical-align: baseline;
  line-height: 0;
  .ec-zipInputHelp__icon {
    display: inline-block;
    margin-top: -10px;
    width:20px;
    height:20px;
    background: #525263;
    border-radius: 50%;
    font-size: 13px;
    position: relative;
    top: -6px;
  .ec-icon img{
      width: 1em;
      height: 1em;
      position: relative;
      left: 3px;
      top: 3px;
    }
  }
  span {
    margin-left: 8px;
    display: inline-block;
    color: #0092C4;
    vertical-align: 3px;
  }
}
.ec-zipAuto {
  margin-bottom: 16px;
  .ec-inlineBtn {
    font-weight: normal;
  }
}

.ec-telInput{
  @extend .ec-input;
  input{
    display: inline-block;
    width: auto;
    font-size: 16px;
    text-align: right;
    max-width: 5em
  }
  span{
    display: inline-block;
    padding: 0 5px 0 3px;
    margin-left:5px;
  }
}

.ec-radio{
  label{
    margin-right:20px;
  }
  input{
    margin-right: 10px;
    margin-bottom: 10px;
  }
  span{
    font-weight: normal;
  }

}

.ec-blockRadio{
  label{
    display: block;
  }
  span {
    padding-left: 10px;
    font-weight: normal;
  }
}

.ec-selects {
  margin-bottom: 20px;
  @include borderBottom;
}
.ec-select{
  @extend .ec-input;
  margin-bottom: 16px;
  select{
    display: inline-block;
    width: auto;
    background-color: rgb(248, 248, 248);
    -webkit-appearance: menulist;
    -moz-appearance: menulist;
    &:focus {
      box-shadow: none;
    }
  }
  label{
    margin-right: 10px;
    font-weight: bold;
  }
  label:nth-child(3){
    margin-left: 10px;
    font-weight: bold;
  }
}
.ec-select__delivery {
  display: block;
  margin-right: 16px;
  @include media_desktop {
    display: inline-block;
  }
}
.ec-select__time {
  display: block;
  @include media_desktop {
    display: inline-block;
  }
}

.ec-birth{
  @extend .ec-input;
  select{
    display: inline-block;
    width: auto;
    margin: 0 0 10px;
    background-color: rgb(248, 248, 248);
    -webkit-appearance: menulist;
    -moz-appearance: menulist;
    &:focus {
      box-shadow: none;
    }
    @include media_desktop{
      margin: 0 8px 10px;
    }
  }
  span{
    margin-left:5px;
  }
}

.ec-label{
  display: inline-block;
  font-weight: bold;
  margin-bottom: 5px;
}

.ec-required{
  display: inline-block;
  margin-left: .8em;
  vertical-align: 2px;
  color: #DE5D50;
  font-size: 12px;
  font-weight: normal;
  @include media_desktop {
    margin-left: 1em;
  }
}

.ec-icon img {
  max-width: 80px;
  max-height: 80px;
}

.ec-grid2{
  @include row;
  & &__cell{
    @include makeSmColumn(6);
  }
  & &__cell2{
    @include makeSmColumn(12);
  }
}

.ec-grid3{
  @include row;
  & &__cell{
    @include makeSmColumn(4);
  }
  & &__cell2 {
    @include makeSmColumn(8);
  }
  & &__cell3 {
    @include makeSmColumn(12);
  }
}

.ec-grid4{
  @include row;
  & &__cell{
    @include makeSmColumn(3);
  }
}

.ec-grid6{
  @include row;
  & &__cell{
    @include makeSmColumn(2);
  }
  & &__cell2{
    @include makeSmColumn(4);
  }
  & &__cell3{
    @include makeSmColumn(6);
  }
}

.ec-off1Grid{
  margin: 0;
  @include media_desktop {
    @include row;
  }
  & &__cell{
    margin: 0;
    @include media_desktop {
      @include makeSmColumn(10);
      margin-left: percentage((1 / 12));
    }
  }
}

.ec-off2Grid{
  @include row;
  & &__cell{
    margin: 0;
    @include media_desktop {
      @include makeSmColumn(8);
      margin-left: percentage((2 / 12));
    }
  }
}

.ec-off3Grid{
  @include row;
  & &__cell{
    margin: 0;
    @include media_desktop {
      @include makeSmColumn(6);
      margin-left: percentage((3 / 12));
    }
  }
}

.ec-off4Grid{
  @include row;
  & &__cell{
    margin: 0;
    @include media_desktop {
      @include makeSmColumn(4);
      margin-left: percentage((4 / 12));
    }
  }
}

.ec-grid--left {
  justify-content: flex-start;
}

.ec-grid--right {
  justify-content: flex-end;
}

.ec-grid--center {
  justify-content: center
}

.ec-imageGrid{
  display: table;
  @include borderTop;
  width: 100%;

  & &__img{
    display: table-cell;
    padding: 10px;
    width: 100px;

    @include media_desktop {
      padding: 10px;
      width: 130px;
    }

    img{
      width: 100%;
    }
  }
  & &__content{
    vertical-align: middle;
    display: table-cell;
    span {
      margin-left: 10px;
    }
    p {
      margin-bottom: 0;
    }
  }
}

.ec-login{
  margin: 0 0 20px;
  padding: 30px 13% 20px;
  height: auto;
  background: #F3F4F4;
  box-sizing: border-box;
  @include media_desktop {
    margin: 0 16px;
    padding: 30px 13% 60px;
  }
  & &__icon {
    text-align: center;
  }
  .ec-icon{
    margin-bottom: 10px;
    img {
      width: 90px;
      height: 90px;
      display: inline-block;
    }
  }
  & &__input {
    margin-bottom: 40px;
    .ec-checkbox {
      span {
        margin-left: 5px;
        font-weight:normal;
      }
    }
  }
  & &__actions {
    color: #fff;
    @include reset_link();
  }
  & &__link {
    margin-top: 5px;
    margin-left: 0;
    @include media_desktop {
      margin-left: 20px;
    }
  }
  .ec-errorMessage {
    color: $clrRed;
    margin-bottom: 20px;
  }
}

.ec-guest{
  display: table;
  margin: 0;
  padding: 13%;
  height: auto;
  box-sizing: border-box;
  background: #F3F4F4;

  @include media_desktop {
    height: 100%;
    margin: 0 16px;
  }
  & &__inner{
    display: table-cell;
    vertical-align: middle;
    text-align: center;
    p {
      margin-bottom: 16px;
    }
  }
  & &__actions {
    display: block;
    vertical-align: middle;
    text-align: center;
    color: #fff;
    @include reset_link();
  }
  & &__icon{
    font-size: 70px;
    text-align: center;
  }
}

.ec-displayB{
  margin-bottom: 24px;
  display: flex;
  justify-content: space-between;
  flex-direction:column;
  @include media_desktop {
    flex-direction:row;
  }
  & &__cell {
    width: 100%;
    margin-bottom: 16px;
    @include reset_link();
    @include media_desktop {
      width: 31.4466%;
      margin-bottom: 0;
    }
    &:hover {
      text-decoration: none;
      img{
        opacity: .8;
      }
      a {
        text-decoration: none;
      }
    }
  }
  & &__img {
    margin-bottom: 15px;
  }

  & &__catch{
    margin-bottom: 15px;
    text-decoration: none;
    font-weight: bold;
    color: #9a947e;
  }
  & &__comment {
    margin-bottom: 14px;
    text-decoration: none;
    color: #525263;
    font-size: 14px;
  }
  & &__link{
    text-decoration: none;
    font-weight: bold;
    color: #9a947e;
  }

}

.ec-displayC{
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  margin-bottom: 24px;
  & &__cell{
    width: 47%;
    @include reset_link();
    @include media_desktop(){
      width: 22.8775%;
    }
    &:hover {
      a {
        text-decoration: none;
      }
      img{
        opacity: .8;
      }
    }
  }
  & &__img{
    display: block;
    width: 100%;
    margin-bottom: 15px;
  }
  & &__catch{
    display: block;
    width: 100%;
    font-weight: bold;
    color: #9a947e;
  }
  & &__title{
    display: block;
    width: 100%;
    color: #525263;
  }
  & &__price{
    display: block;
    width: 100%;
    font-weight: bold;
    color: #525263;
  }
  & &__price--sp{
    display: block;
    width: 100%;
    font-weight: bold;
    color: #DE5D50;
  }
}

.ec-displayD {
  display:flex;
  justify-content:space-between;
  flex-wrap:wrap-reverse;
  @include media_desktop(){
    box-sizing: border-box;
    flex-wrap:nowrap;
  }

  & &__cell{
    width: 30%;
    margin-bottom: 8px;
    @include reset_link();
    @include media_desktop(){
      width: 14.3083%;
      margin-bottom: 16px;
    }
    &:hover {
      text-decoration: none;
      img{
        opacity: .8;
      }
    }
  }
  & &__img{
    display: block;
    width: 100%;
  }
}

.ec-topicpath{
  letter-spacing: -.4em;
  -webkit-margin-before: 0;
  -webkit-margin-after: 0;
  -webkit-margin-start: 0;
  -webkit-margin-end: 0;
  -webkit-padding-start: 0;
  border-top: 1px solid #ccc;
  border-bottom: 1px dotted #ccc;
  padding: 10px;
  list-style: none;
  overflow: hidden;
  font-size: 12px;
  color: #0092C4;
  @include media_desktop {
    padding: 30px 0 10px;
    border: 0;
    font-size: 16px;
  }

  & &__item {
    @include reset_link();
  }
  & &__divider{
    color: #000;
  }
  & &__item,
  & &__divider,
  & &__item--active{
    display: inline-block;
    min-width: 16px;
    text-align: center;
    position: relative;
    letter-spacing: initial;
  }
  & &__item--active{
    font-weight: bold;
    @include reset_link();
  }
}

.ec-pager{
  list-style: none;
  list-style-type: none;
  margin: 0 auto;
  padding: 1em 0;
  text-align: center;
  & &__item,
  & &__item--active{
    display: inline-block;
    min-width: 29px;
    padding: 0 3px 0 2px;
    text-align: center;
    position: relative;
    @include reset_link();
    a{
      color: inherit;
      display: block;
      line-height: 1.8;
      padding: 5px 1em;
      text-decoration: none;
    }
    a:hover{
      color: inherit;
    }
  }
  & &__item--active {
    background: $clrGray;
  }
  & &__item:hover{
    background: $clrGray;
  }

}

.ec-progress{
  margin: 0 auto;
  padding: 8px 0 16px;
  display: table;
  table-layout: fixed;
  width: 100%;
  max-width: 600px;
  list-style: none;
  @include media_desktop {
    margin-bottom: 30px;
    padding: 0;
  }

  & &__item{
    display:table-cell;
    position: relative;
    font-size: 14px;
    text-align: center;
    font-weight: bold;
    z-index: 10;

    &:after {
      content: '';
      position: absolute;
      display: block;
      background: #525263;
      width: 100%;
      height: 0.25em;
      top: 1.25em;
      left: 50%;
      margin-left: 1.5em\9;
      z-index: -1;
    }
    &:last-child:after {
      display: none;
    }
  }
  & &__number{
    line-height: 30px;
    width: 30px;
    height: 30px;
    margin-bottom: 5px;
    font-size: 12px;
    background: #525263;
    color: #fff;
    top: 0;
    left: 18px;
    display: inline-block;
    text-align: center;
    vertical-align: middle;
    border-radius: 50%;
    @include media_desktop(){
      line-height: 42px;
      width: 42px;
      height: 42px;
      font-size: 20px;
    }
  }
  & &__label {
    font-size: 12px;
  }
  .is-complete {
    .ec-progress__number {
      background: #5CB1B1;
    }
    .ec-progress__label {
      color: #5CB1B1;
    }
  }
}

.ec-cartNavi{
  border: 1px solid #D9D9D9;
  width: 256px;
  position: relative;
  display: block;
  height: 44px;
  overflow: hidden;
  white-space: nowrap;
  z-index: 3;
  cursor: pointer;
  & &__icon {
    width: 30px;
    height: 30px;
    position: relative;
    top: 8px;
    left: 8px;
    @include fadeIn(inline-block,200ms);
  }
  & &__iconClose {
    width: 25px;
    height: 25px;
    position: absolute;
    top: 8px;
    left: 8px;
    @include fadeOut(200ms);
  }
  & &__badge{
    position: absolute;
    left: 45px;
    top: 13px;
    display: inline-block;
    min-width: 10px;
    padding: 3px 7px;
    font-size: 10px;
    font-weight: 700;
    line-height: 1;
    color: #fff;
    text-align: center;
    white-space: nowrap;
    vertical-align: baseline;
    background-color: #DE5D50;
    border-radius: 10px;
    @include fadeIn(inline-block);
  }
  & &__label{
    font-size: 12px;
    position: absolute;
    right:0;
    top:0;
    height: 44px;
    line-height: 44px;
    vertical-align: middle;
    margin-right: 16px;
  }
  & &__price{
    display: inline-block;
    font-size: 16px;
  }
}
.is_active {
  .ec-cartNavi__icon{
    @include fadeOut;
  }
  .ec-cartNavi__badge{
    display: none;
  }
  .ec-cartNavi__iconClose {

    @include fadeIn(inline-block,200ms);
  }
}

.ec-cartNaviNull {
  display: none;
  min-width: 256px;
  text-align: center;
  background: #f3f3f3;
  box-sizing: border-box;
  padding: 16px;
  z-index: 3;
  position: absolute;
  @include fadeOut;
  & &__message {
    border: 1px solid #D9D9D9;
    padding: 16px 0;
    font-size: 16px;
    font-weight: bold;
    color: #fff;
    background-color: #F99;
    p {
      margin: 0;
    }
  }
}

.is_active {
  .ec-cartNaviNull {
    @include fadeIn;
    display: block;
  }
  .ec-cartNaviIsset {
    @include fadeIn;
    display: block;
  }
}

.ec-cartNaviIsset {
  display: none;
  min-width: 256px;
  max-width:256px;
  text-align: center;
  background: #f3f3f3;
  box-sizing: border-box;
  padding: 16px;
  z-index: 3;
  position: absolute;
  @include fadeOut;
  & &__cart {
    @include clearfix;
    border-bottom: 1px solid #E8E8E8;
    margin-bottom: 16px;
    padding-bottom: 32px;
  }
  & &__cartImage {
    float: left;
    width: 45%;
    img {
      width: 100%;
    }
  }
  & &__cartContent {
    float: right;
    width: 55%;
    padding-left: 16px;
    text-align:left;
    box-sizing:border-box;
  }
  & &__action {
    .ec-blockBtn--action {
      color:#fff;
      margin-bottom: 8px;
    }
  }
  & &__cartContentTitle {
    margin-bottom: 8px;
  }
  & &__cartContentPrice {
    font-weight: bold;
  }
  & &__cartContentTax {
    display: inline-block;
    font-size: 12px;
    font-weight: normal;
    margin-left: 2px;
  }
  & &__cartContentNumber {
    font-size: 14px;
  }
}

