section.css:
.ec-totalBox{
  background:#F3F3F3;
  padding: 16px;
  margin-bottom: 16px;
  & &__spec{
    position: relative;
    margin-bottom:8px;
    dt{
      font-weight: normal;
      position: relative;
      top:0;
      left: 0;
      text-align: left;
    }
    dd{
      position: absolute;
      top:0;
      right: 0;
      text-align: right;

    }
    & &__specTotal {
      color: $clrRed;
    }
  }
  & &__total{
    border-top: 1px dotted #ccc;
    padding: 8px 0;
    text-align: right;
    font-size: 14px;
    font-weight:bold;
  }
  & &__price{
    margin-left: 16px;
    font-size: 16px;
    font-weight:bold;
    color: $clrRed;
    @include media_desktop {
      font-size: 24px;
    }
  }
  & &__taxLabel {
    margin-left: 8px;
    color: $clrRed;
    font-size: 12px;
    @include media_desktop {
      font-size: 14px;
    }
  }
  & &__btn {
    @include reset_link();
    color: #fff;
    .ec-blockBtn--action {
      font-size: 16px;
      font-weight: bold;
    }
  }
}

.ec-news {
  margin-bottom: 16px;
  background: #F8F8F8;
  @include media_desktop {
    margin-right: 3%;
  }
  @include media_desktop {
    margin-bottom: 32px;
  }
  & &__title{
    font-weight: bold;
    padding: 8px;
    font-size: 16px;
    text-align: center;
    @include media_desktop {
      padding: 16px;
      text-align: left;
      font-size: 24px;
    }
  }
  & &__items{
    padding: 0;
    list-style: none;
    border-top: 1px dotted #ccc;
  }
}

.ec-newsline {
  display: flex;
  flex-wrap:wrap;
  overflow: hidden;
  padding: 0 16px;
  & &__info{
    width: 100%;
    padding: 16px 0;
    @include clearfix;
  }
  & &__date{
    display: inline-block;
    margin-right: 10px;
    float: left;
  }
  & &__comment{
    display: inline-block;
    float: left;
  }
  & &__close{
    float: right;
    display: inline-block;
    text-align: right;
    .ec-closeBtn--circle {
      display: inline-block;
      width: 25px;
      height: 25px;
      min-width: 25px;
      min-height: 25px;

    }
  }
  & &__description{
    width: 100%;
    height: 0;
    transition: all .2s ease-out;
  }

  &.is_active &__description{
    height: auto;
    transition: all .2s ease-out;
    padding-bottom: 16px;
  }
  &.is_active .ec-icon img {
    transform: rotateX(180deg);
  }
}

.ec-banner{
  padding: 16px;
  margin-bottom: 16px;
  background: #F8F8F8;
  text-align: center;
  font-weight: bold;
  border: 1px solid #ccc;
  @include media_desktop {
    margin-left: 3%;
    margin-bottom: 16px;
  }
  & &__intro {
    color: #DE5D50;
    font-size: 20px;
    font-weight: bold;
  }
  & &__title {
    color: #DE5D50;
    font-size: 32px;
    font-weight: bold;
  }
}

.ec-navlistRole{
  & &__navlist {
    @include reset_link;
    display: flex;
    flex-wrap: wrap;
    border-color: #D0D0D0;
    border-style: solid;
    border-width: 1px 0 0 1px;
    margin-bottom: 32px;
    padding: 0;
    list-style: none;
    @include media_desktop {
      flex-wrap: nowrap;
    }
  }

  & &__item{
    width: 50%;
    border-color: #D0D0D0;
    border-style: solid;
    border-width: 0 1px 1px 0;
    text-align: center;
    font-weight: bold;
    a {
      padding: 16px;
      width: 100%;
      display: inline-block;
      &:hover{
        background: #f5f7f8;
      }
    }
  }
  .active {
    a {
      color: #DE5D50;
    }
  }
}

.ec-welcomeMsg{
  @include mypageContainer;
  margin: 1em 0;
  padding-bottom: 32px;
  text-align: center;
  @include borderBottom;

}

.ec-favoriteRole{
  & &__header {
    margin-bottom: 16px;
  }
  & &__detail {
  }
  & &__itemList {
    @include reset_link;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    padding: 0;
    list-style: none;
  }
  & &__item{
    margin-bottom: 8px;
    width: 47.5%;
    position: relative;
    @include media_desktop(){
      width: 22.8142%;
      margin-bottom: 24px;
    }
    img{
      width: 100%;
    }
    .ec-closeBtn--circle {
      position: absolute;
      right: 10px;
      top: 10px;
      .ec-icon img{
        width: 1em;
        height: 1em;
      }
    }
  }
  & &__itemThumb {
    display: block;
    height:auto;
    margin-bottom: 8px;
  }
  & &__itemTitle{
    margin-bottom: 2px;
  }
  & &__itemPrice{
    font-weight: bold;
    margin-bottom: 0;
  }

}

.ec-role{
  @include container;
}

.ec-mypageRole{
  @include mypageContainer;

  .ec-pageHeader h1{
    @include media_desktop {
      margin: 10px 0 48px;
      padding: 8px 0 18px;
    }
  }

}

.ec-layoutRole{
  width: 100%;
  transition: transform 0.3s;
  background: #fff;
  & &__contentTop {
    padding: 0;
  }

  & &__contents{
    margin-right: auto;
    margin-left: auto;
    width: 100%;
    max-width: 1150px;
    display: flex;
    flex-wrap: nowrap;

  }
  & &__main{
    width: 100%;
  }
  & &__mainWithColumn{
    width: 100%;
    @include media_desktop(){
      width: 75%;
    }
  }
  & &__mainBetweenColumn{
    width: 100%;
    @include media_desktop(){
      width: 50%;
    }
  }
  & &__left,
  & &__right{
    display: none;
    @include media_desktop(){
      display: block;
      width: 25%;
    }
  }
}

.ec-layoutRole.is_active{
  transform: translateX(-260px);
  transition: transform 0.3s;
  z-index: 10;
  @include media_desktop(){
    transform: translateX(0px);
  }
}
.ec-headerRole{
  @include container;
  padding-top: 15px;
  position: relative;
  &:after {
    display: none;
  }
  @include media_desktop {
    @include clearfix;
  }
  &::before{
    display: none;
  }
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  width: auto;
  @include media_desktop {
    width: 100%;
    @include clearfix;
  }
  & &__title{
    width: 73%;
    @include media_desktop() {
      width: 100%;
    }
  }
  & &__cart{
    display: none;
    position: absolute;
    top:15px;
    right: 0;
    @include media_desktop(){
      display: block;
      @include reset_link;
    }
  }
  & &__navSP{
    display: block;
    position: absolute;
    top:15px;
    width: 27%;
    right: 0px;
    text-align: right;
    @include media_desktop {
      display: none;
    }
  }
}
.ec-headerNaviRole{
  @include container;
  @include clearfix;
  & &__search{
    display: none;
    @include media_desktop(){
      display: block;
      float: left;
      width: 370px;
      @include reset_link;
    }
  }
  & &__nav{
    display: none;
    @include media_desktop(){
      display: block;
      float: right;
      width: 50%;
      @include reset_link;
    }
  }

}
.ec-headerNavSP {
  margin-right: 10px;
  width: 100%;
  height: auto;
  vertical-align: top;
  text-align: right;
  & &__item{
    display: inline-block;
    margin-right: 10px;
    width: auto;
    text-align: center;
  }
  & &__item:nth-of-type(2){
    margin-left: 5px;
  }
  .ec-cartNaviSP{
    display: inline-block;
    font-size: 30px;
    cursor: pointer;
    position: relative;
    .ec-cartNaviSP__icon{
      display: inline-block;
      width: 30px;
      height: 30px;
      color: $clrDarkGray;
    }
    .ec-cartNaviSP__badge{
      display: inline-block;
      padding: 3px 7px;
      min-width: 10px;
      font-size: 10px;
      font-weight: 700;
      line-height: 1;
      color: #fff;
      text-align: center;
      white-space: nowrap;
      vertical-align: baseline;
      background: #DE5D50;
      border-radius: 10px;
      position: absolute;
      top: 0;
      right: -10px;
    }
  }
  & &__itemMenu {
    display: block;
    position: relative;
    width: 25px;
    height: 15px;
    cursor: pointer;
    span {
      display: inline-block;
      transition: all .4s;
      box-sizing: border-box;
      position: absolute;
      left: 0;
      width: 100%;
      height: 3px;
      background: #525263;
    }
    span:nth-of-type(1) {
      top: 0;
      transform: translateY(0) rotate(0);
      transition: all .3s;
    }
    span:nth-of-type(2) {
      top: 6px;
      opacity: 1;
      transition: all .3s;
    }
    span:nth-of-type(3) {
      bottom: 0;
      transform: translateY(0) rotate(0);
      transition: all .3s;
    }
  }
}
.have_curtain .ec-headerNavSP__itemMenu {
  span:nth-of-type(1) {
    transform: translateY(2px) rotate(-45deg);
    transition: all .3s;
  }
  span:nth-of-type(2) {
    opacity: 0;
    transition: all .3s;
  }
  span:nth-of-type(3) {
    transform: translateY(-10px) rotate(45deg);
    transition: all .3s;
  }
}

.ec-headerTitle{
  @include commonStyle();
  & &__title {
    h1 {
      margin: 0;
      padding: 0;
    }
    a{
      display: inline-block;
      margin-bottom: 15px;
      text-decoration: none;
      font-size: 20px;
      @include media_desktop(){
        margin-bottom: 30px;
        font-size: 40px;
      }
      font-weight: bold;
      color: #000;

      &:hover {
        color: #666;
      }
    }
  }
  & &__subtitle {
    font-size: 10px;
    @include media_desktop(){
      font-size: 16px;
      margin-bottom: 10px;
    }
    a {
      display: inline-block;
      color: #0092C4;
      text-decoration: none;
      cursor: pointer;
    }
  }
}

.ec-headerNav{
  text-align: right;
  & &__item{
    margin-left: 0;
    display: inline-block;
    font-size: 28px;
    line-height: 50px;
    @include media_desktop {
      margin-left: 10px;
    }
    @include reset_link;
    span{
      font-size: 14px;
    }
    .ec-icon {
      img {
        width: 30px;
        height: 30px;
        vertical-align: middle;
        margin-right: 5px;
      }
    }
  }
}

.ec-headerSearch{
  @include clearfix;
  & &__category {
    float: none;
    @include media_desktop {
      float: left;
      width: 43%;
    }
    .ec-select {
      margin-bottom: 6px;
    }
    select{
      width: auto;
      max-width: 100%;
      height: 50px;
      border: 1px solid #ccc;
      font-size: 1.6rem;
      padding: 0.5em 1em;
      border-radius: 5px;
      box-shadow: none;
      box-sizing: border-box;
      -webkit-appearance: menulist;
      appearance: menulist;
      @include media_desktop {
        max-width: 180px;
      }
    }
  }
  & &__keyword{
    float: none;
    height: 50px;
    padding: 0 40px 0 10px;
    border-bottom: 1px solid #ccc;
    position: relative;
    color: $clrDarkGray;
    @include media_desktop {
      float: right;
      width: 57%;
    }
    input[type="search"]{
      height: 50px;
      width: 100%;
      font-size: 1.6rem;
      border: 0 none;
      padding: 0.5em 0;
      box-shadow: none;
      background: none;
      box-sizing: border-box;
    }
    .ec-icon {
      width: 25px;
      height: 25px;
    }
  }
  & &__keywordBtn{
    border: 0;
    background: none;
    position: absolute;
    right: 0;
    top: 6px;
    display: block;
    font-size: 25px;
    overflow: hidden;
    white-space: nowrap;
    z-index: 1;
  }
}

.ec-cateoryNaviRole{
  @include container;
  display: none;
  @include media_desktop(){
    display: block;
    width: 100%;
    @include reset_link;
  }
}
.ec-itemNav {
  display: block;
  border: 0;
  margin: 0;
  padding: 0;
  width: 100%;
  text-align: center;
  list-style: none;
  position: relative;
  @include media_desktop {
    margin: 20px 0 30px;
    border-bottom: 1px solid #D9D9D9;
    border-top: 1px solid #D9D9D9;
  }
  & &__item {
    display: inline-block;
    font-weight: bold;
    @include reset_link;
    &:hover {
      background: #fafafa;
    }
    a {
      display: inline-block;
      margin-top: 0;
      padding: 16px;
      height: 55px;
    }
    &:hover > .ec-itemNavAccordion {
      overflow: visible;
      height: 50px;
      transition:all .2s ease-out;
      .ec-itemNavAccordion__item {
        height: 50px;
        transition:all .2s ease-out;
      }
    }
  }
  .ec-itemNavAccordion {
    z-index: 1000;
    position: absolute;
    padding: 0;
    height: 0;
    overflow: hidden;
    transition:all .2s ease-out;
    .ec-itemNavAccordion__item {
      border-bottom: 1px solid #ccc;
      list-style: none;
      height: 0;
      transition:all .2s ease-out;
      a {
        list-style: none;
        border-top: 1px solid #E8E8E8;
        border-bottom: 1px solid #E8E8E8;
        margin-top: 0;
        padding: 15px 10px;
        min-width: 200px;
        width: 100%;
        text-align: left;
        background: #fff;
        transition:all .5s ease-out;
        &:hover {
          background: #fafafa;
        }
      }
    }
  }
}

.ec-drawerRole{
  overflow-y:scroll;
  display: block;
  background: #F6F6F6;
  position: fixed;
  top:0;
  width: 260px;
  height:100vh;
  right:0;
  z-index: -1;
  transition: z-index 0ms 1ms;
  @include media_desktop(){
    display: none;
  }
}

.ec-drawerRole.is_active{
  z-index: 1;
  transition: z-index 0ms 300ms;
  @include media_desktop(){
    display: none;
  }
}

.ec-drawerRole{
   .ec-headerSearch{
    padding: 16px 8px 26px;
    background: #EBEBEB;
    color: #636378;
    select{
      width: 100% !important;
    }
  }
   .ec-headerNav{
    .ec-headerNav__item{
      text-align: left;
      display: block;
      padding-left: 10px;
      height: 60px;
      border-bottom: 1px solid #e8e8e8;
      line-height: 50px;
      font-size: 30px;
      @include media_desktop {
        padding:0 10px;
        height: 50px;
      }
    }

  }
  .ec-itemNav{
    .ec-itemNav__item{
      display: block;
      text-align: left;
      border-bottom: 1px solid #e8e8e8;
      line-height: 50px;
      font-weight: bold;
      a {
        padding:0 10px;
        height: 50px;
      }
    }
    .ec-itemNavAccordion {
      display: none;
    }
  }
}
.ec-overlayRole{
  position: fixed;
  width: 100%;
  height: 100vh;
  top: 0px;
  left: 0;
  opacity: 0;
  background: transparent;
  transform: translateX(0);
  transition: all .3s;
  visibility:hidden;
}
.have_curtain .ec-overlayRole {
  display: block;
  opacity: 1;
  background: rgba(0,0,0, 0.5);
  transform: translateX(-260px);
  transition: all .3s;
  visibility: visible;
}

.ec-itemNavAccordion {
  display: none;
}

.ec-footerRole{
  background: #f8f8f8;
  border-top: 1px solid #e8e8e8;
  padding-top: 12px;
  padding-bottom: 24px;
  margin-top: 36px;

  @include media_desktop(){
    padding-top: 32px;
    margin-top: 80px;
  }
  & &__inner{
    @include container;
  }
}

.ec-footerNavi{
  list-style: none;
  text-align: center;
  padding: 0;
  & &__link{
    display: inline-block;
    margin: 0 8px 8px;
    a{
      color: inherit;
      text-decoration: none;
    }
    &:hover {
      a {
        color: #686868;
        text-decoration: none;
      }

    }

  }
}

.ec-footerTitle{
  margin-top:20px;
  text-align: center;
  @include media_desktop {
    margin-top:80px;
  }
  & &__logo{
    font-weight: bold;
    @include reset_link();
    a{
      font-size: 16px;
      color: inherit;
      @include media_desktop {
        font-size: 2em;
      }
    }
    &:hover {
      a {
        color: #686868;
        text-decoration: none;
      }
    }
  }
  & &__copyright{
    font-size: 10px;
    @include media_desktop {
      font-size: .8em;
    }
  }
}

.ec-sliderRole{
  @include container;
  margin-bottom: 24px;
  ul{
    padding: 0;
    list-style: none;
  }
}
.ec-sliderItemRole{
  @include container;
  margin-bottom: 24px;
  ul{
    padding: 0;
    list-style: none;
  }
  .item_nav {
    display: none;
    @include media_desktop {
      display: flex;
      justify-content: space-between;
      margin-bottom: 0;
    }

  }
  .slideThumb{
    margin-bottom: 25px;
    opacity: .8;
    &:focus {
      outline: none;
    }
    &:hover {
      opacity: 1;
    }
    img {
      width: 80%;
    }
  }
}

.ec-eyecatchRole {
  @include container;
  margin-bottom: 30px;
  text-align: center;
  & &__intro {
    box-sizing: border-box;
